                            // If possible to send target messages => No need for broadcast

// Function declaration

// returns if it is possible to move in that direction (0 = UP, 1 = DOWN, 2 = RIGHT, 3 = LEFT)
bool can_move(int direction){
    Position current_position = c_position[civilian_id];
    switch (direction) {
        case 0: // up
            return current_position.r < N_ROW - 1 && grid[current_position.r + 1][current_position.c] == FREE;
        case 1: // down
            return current_position.r > 0 && grid[current_position.r - 1][current_position.c] == FREE;
        case 2: // right
            return current_position.c < N_COL - 1 && grid[current_position.r][current_position.c + 1] == FREE;
        case 3: // left
            return current_position.c > 0 && grid[current_position.r][current_position.c - 1] == FREE;
        default:
            return false;
    }
}

// move in the provided direction (0 = LEFT, 1 = UP, 2 = RIGHT, 3 = DOWN)
void move(int direction) {
    Position current_position = c_position[civilian_id];
    grid[current_position.r][current_position.c] = 0; // free the old position

    switch (direction) {
        case 0: // up
            c_position[person_id].r++;
            map[current_position.r + 1][current_position.c] = 1; // occupy the new position
            break;
        case 1: // down
            c_position[person_id].r--;
            map[current_position.r - 1][current_position.c] = 1; // occupy the new position
            break;
        case 2: // right
            c_position[person_id].c++;
            map[current_position.r][current_position.c + 1] = 1; // occupy the new position
            break;
        case 3: // left
            c_position[person_id].c--;
            map[current_position.r][current_position.c - 1] = 1; // occupy the new position
            break;
    }
}

template.declaration:
int direction = 1; // current movement direction of the drone (1 = UP, -1 = DOWN)

// Drone moving policy
void move() { 

    // get current drone position
    Position current_position = d_position[drone_id];

    // if the drone is at the top or the bottom of the grid change direction
    if (current_position.r == N_ROW - 1) {
        direction = -1;
        return;
    } else if (current_position.r == 0) {
        direction = 1;
        return;
    }

    // fly in the direction 
    d_position[drone_id].r = d_position[drone_id].r + direction; // update the drone's position  

}

